mapper.select:

	select * from eatery where eat_food_taste in 	
	 
		#{item}
	
		order by RAND() LIMIT #{day}


select * from place where plac_thema like CONCAT('%',#{plac_thema},'%') and 	

	plac_detail_thema like CONCAT('%',#{item},'%')
 
	order by RAND() LIMIT #{day}

SELECT DISTINCT p.p_no, p.p_file, p.p_nation, p.p_city, p.p_title, u.u_nickname, r.child_fee, p.p_due_date
FROM package p 
LEFT JOIN user u
ON p.u_id = u.u_id
LEFT JOIN reward r
ON p.p_no = r.p_no

	
	
		
			p.p_theme = #{param}
		
		
			p.p_nation = #{param}
		
	
	


	ORDER BY p.p_no DESC;


	ORDER BY p.p_due_date DESC;

SELECT DISTINCT l.*, u.u_nickname FROM location_review l LEFT JOIN user u ON l.u_id = u.u_id LEFT JOIN place p ON l.pl_eat_no = p.plac_no LEFT JOIN eatery e ON l.pl_eat_no = e.eat_no
l.lr_contents like concat('%', #{search}, '%')
ORDER BY l.lr_no DESC;
ORDER BY l.lr_star DESC;
